import React, { useState } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend } from 'recharts';
import data from "./data/surprise_songs.json";

const PieChartComponent = () => {
  const [activeSlice, setActiveSlice] = useState<number | null>(null);

  const handlePieClick = (data: any, index: number) => {
    setActiveSlice(index);
  };

  const renderLabel = (entry: any) => {
    const { album, songs, font } = entry;
    const fontFamily = font ? font : "Arial";

    if (entry.index === activeSlice) {
      return (
        <text fontFamily={fontFamily} fontSize={12} fill="black">
          {`${album}: ${songs} Songs`}
        </text>
      );
    }

    return (
      <text fontFamily={fontFamily} fontSize={2} fill="white">
        {album}
      </text>
    );
  };

  return (
    <ResponsiveContainer width="100%" height={400}>
      <PieChart>
        <Pie
          dataKey="songs"
          data={data}
          cx="50%"
          cy="50%"
          outerRadius={100}
          fill="white"
          label={renderLabel}
          onClick={handlePieClick}
        >
          {
            data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))
          }
        </Pie>
        <Legend align="center" />
      </PieChart>
    </ResponsiveContainer>
  );
};

export default PieChartComponent;
