import React from 'react';
import Map from './Map';

const App: React.FC = () => {

  return (
    <div>
      {/* Header */}
      <div className="m-5">
        <div>
          <h1 className="font-bold text-5xl" style={{ fontFamily: 'era', textAlign: 'center' }}>TAYLOR SWIFT: THE ERAS TOUR</h1>
          <h1 className="font-bold text-2xl" style={{ fontFamily: 'era', textAlign: 'center' }}>Eras Tour Tracker</h1>
        </div>
      </div>

      <div className="flex flex-col md:flex-row" style={{ backgroundColor: 'whitesmoke' }}>
        <div className="shadow-md items-center m-5" style={{width:"75%"}}>
          <Map />
        </div>

        {/* Start of Legend */}
        <div className="bg-white shadow-md flex flex-col " style={{ padding: 20, width: '25%' }}>
          <h2 className="mb-4 text-xl items-center">Legend</h2>
          <div className="flex justify-left items-left space-x-2 mb-2">
            <div className="w-4 h-4 bg-red-500 rounded-full"></div>
            <span className="text-gray-700">Tour Locations</span>
          </div>
          <div className="flex items-left space-x-2 mb-2">
            <div className="w-4 h-4 bg-blue-500 rounded-full flex flex-col"></div>
            <span className="text-gray-700">Future Locations</span>
          </div>
          <div className="flex items-left space-x-2 mb-2">
            <div className="w-4 h-4 bg-green-500 rounded-full"></div>
            <span className="text-gray-700">Completed Locations</span>
          </div>
        </div>
        {/* End of Legend */}
      </div>
    </div>
  );
};

export default App;
