import React, { useEffect, useState } from 'react';
import L, { Marker } from 'leaflet';
import 'leaflet/dist/leaflet.css';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

import markerIcon from './marker-icon.png'; // Import a custom marker icon if desired
import futureIcon from './future-marker-icon.png';
import completedIcon from './completed-marker-icon.png';

import markerData from './data/date_testing.json';

interface MarkerData {
  lat: number;
  lon: number;
  city: string;
  date: string;
}

const MapComponent: React.FC = () => {
  const [sliderValue, setSliderValue] = useState(0);
  const [map, setMap] = useState<L.Map | null>(null);
  const [markers, setMarkers] = useState<Marker[]>([]);
  const [markerVisibility, setMarkerVisibility] = useState<boolean[]>([]);

  useEffect(() => {
    const newMap = L.map('map').setView([33.529115, -112.264551], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors',
      minZoom: 0,
      maxZoom: 30,
    }).addTo(newMap);

    const customIcon = L.icon({
      iconUrl: markerIcon, // Custom marker icon URL
      iconSize: [80, 112], // size of the icon
      iconAnchor: [40, 90], // point of the icon which will correspond to marker's location
      popupAnchor: [-3, -76],
    });

    const sortedMarkerData = [...markerData].sort(
      (a: MarkerData, b: MarkerData) =>
        new Date(a.date).getTime() - new Date(b.date).getTime()
    ); // Sort markers by date

    const newMarkers: Marker[] = [];
    sortedMarkerData.forEach((marker, index) => {
      const { lat, lon, city, date } = marker;
      const markerOptions: L.MarkerOptions = {
        icon: customIcon,
      };

      const currentDate = new Date();
      const markerDate = new Date(date);

      if (markerDate <= currentDate) {
        // Completed date
        markerOptions.icon = L.icon({
          ...customIcon.options,
          iconUrl: completedIcon, // Set completed marker icon URL
        });
      } else {
        // Future date
        markerOptions.icon = L.icon({
          ...customIcon.options,
          iconUrl: futureIcon, // Set future marker icon URL
        });
      }

      const mapMarker = new Marker([lat, lon], markerOptions)
        .bindPopup(`<b>${city}</b><br>Date: ${date}`);

      newMarkers.push(mapMarker);
      setMarkerVisibility((prevVisibility) => [...prevVisibility, true]);
    });

    setMarkers(newMarkers);
    setSliderValue(sortedMarkerData.length - 1); // Set slider value to the maximum
    setMap(newMap);

    return () => {
      newMap.remove();
    };
  }, []);

  const handleSliderChange = (value: number | number[]) => {
    if (typeof value === 'number' && map) {
      setSliderValue(value);

      // Update the visibility state of each marker
      setMarkerVisibility((prevVisibility) =>
        prevVisibility.map((_, index) => index <= value)
      );
    }
  };

  useEffect(() => {
    if (map) {
      markers.forEach((marker, index) => {
        if (markerVisibility[index]) {
          marker.addTo(map);
        } else {
          marker.removeFrom(map);
        }
      });
    }
  }, [map, markers, markerVisibility]);

  return (
    <div>
      <Slider
        min={0}
        max={markerData.length - 1}
        value={sliderValue}
        onChange={handleSliderChange}
      />
      <div id="map" style={{ height: '500px', width: '100%' }}></div>
    </div>
  );
};

export default MapComponent;
