import React, { useState } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend } from 'recharts';
import data from "./data/surprise_songs.json";

const PieChartComponent = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const handlePieClick = (data, index) => {
    setActiveIndex(index);
  };

  return (
    <ResponsiveContainer width="100%" height={400}>
      <PieChart>
        <Pie
          dataKey="songs"
          data={data}
          cx="50%"
          cy="50%"
          outerRadius={100}
          fill="white"
          label={(entry) => entry.album}
          onMouseEnter={handlePieClick} // Add onMouseEnter event handler
          activeIndex={activeIndex} // Set the activeIndex prop
          activeShape={({ cx, cy, innerRadius, outerRadius, startAngle, endAngle, fill }) => {
            const RADIAN = Math.PI / 180;
            const { songs } = data[activeIndex];

            return (
              <text
                x={cx}
                y={cy}
                dy={8}
                textAnchor="middle"
                fill={fill}
              >
                {`${songs} Songs`}
              </text>
            );
          }}
        >
          {
            data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))
          }
        </Pie>
        <Legend align="center" />
      </PieChart>
    </ResponsiveContainer>
  );
};

export default PieChartComponent;
