import React, { useEffect } from 'react';
import L, { Marker } from 'leaflet';
import 'leaflet/dist/leaflet.css';

import markerIcon from './marker-icon.png'; // Import a custom marker icon if desired
// import markerShadow from './marker-shadow.png'; // Import a custom marker shadow if desired

import markerData from './data/na_leg_res.json';


const Map: React.FC = () => {
	useEffect(() => {
		const map = L.map('map').setView([33.529115,-112.264551], 13);


		L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
			attribution: 'Map data &copy; OpenStreetMap contributors',
			minZoom: 0,
			maxZoom: 30,
		}).addTo(map);



		const customIcon = L.icon({
			iconUrl: markerIcon, // Custom marker icon URL
			//   shadowUrl: markerShadow, // Custom marker shadow URL
			iconSize: [80,112], // size of the icon
			// shadowSize: [50, 64], // size of the shadow
			iconAnchor: [40, 90], // point of the icon which will correspond to marker's location
			shadowAnchor: [4, 62],  // the same for the shadow
			popupAnchor: [-3, -76]
		});


		markerData.forEach((marker) => {
			const { lat, lon, city } = marker;
			const markerOptions = { icon: customIcon };

			const mapMarker = new Marker([lat, lon], markerOptions)
				.bindPopup(`<b>${city}</b><br>`)
				.addTo(map);
		});

		return () => {
			map.remove();
		};
	}, []);

	return <div id="map" style={{ height: "400px", width:"75%", margin:"50", padding:"30"}}>

	</div>;
};

export default Map;
