import React, { useEffect, useState } from 'react';
import L, { Marker } from 'leaflet';
import 'leaflet/dist/leaflet.css';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

import markerIcon from './marker-icon.png'; // Import a custom marker icon if desired

import markerData from './data/date_testing.json';

interface MarkerData {
  lat: number;
  lon: number;
  city: string;
  date: string;
}

const Map: React.FC = () => {
  const [sliderValue, setSliderValue] = useState<number>(0);
  const [markers, setMarkers] = useState<Marker[]>([]);

  useEffect(() => {
    const map = L.map('map').setView([33.529115, -112.264551], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors',
      minZoom: 0,
      maxZoom: 30,
    }).addTo(map);

    const customIcon = L.icon({
      iconUrl: markerIcon, // Custom marker icon URL
      iconSize: [80, 112], // size of the icon
      iconAnchor: [40, 90], // point of the icon which will correspond to marker's location
      popupAnchor: [-3, -76],
    });

    const newMarkers: Marker[] = [];

    markerData.sort((a: MarkerData, b: MarkerData) => new Date(a.date).getTime() - new Date(b.date).getTime()); // Sort markers by date

    markerData.forEach((marker, index) => {
      const { lat, lon, city, date } = marker;
      const markerOptions = { icon: customIcon };

      const mapMarker = new Marker([lat, lon], markerOptions)
        .bindPopup(`<b>${city}</b><br>Date: ${date}`)
        .addTo(map);

      newMarkers.push(mapMarker);
    });

    setMarkers(newMarkers);

    return () => {
      map.remove();
    };
  }, []);

  const handleSliderChange = (value: number | number[]) => {
    if (typeof value === 'number') {
      setSliderValue(value);

      markers.forEach((marker, index) => {
        if (index <= value) {
          marker.addTo(marker._map);
        } else {
          marker.removeFrom(marker._map);
        }
      });
    }
  };

  return (
    <div>
      <Slider
        min={0}
        max={markerData.length - 1}
        value={sliderValue}
        onChange={handleSliderChange}
      />
      <div id="map" style={{ height: '500px', width: '100%' }}></div>
    </div>
  );
};

export default Map;
