import React, { useEffect } from 'react';
import L, { Marker } from 'leaflet';
import 'leaflet/dist/leaflet.css';

import markerIcon from './images/marker-icon.png'; // Import a custom marker icon if desired
// import markerShadow from './marker-shadow.png'; // Import a custom marker shadow if desired

import markerData from './data/na_leg_res.json';

const Map: React.FC = () => {
	useEffect(() => {
		const map = L.map('map').setView([51.505, -0.09], 13);

		L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
			attribution: 'Map data &copy; OpenStreetMap contributors',
			minZoom: 0,
			maxZoom: 30,
		}).addTo(map);

		const customIcon = L.icon({
			iconUrl: markerIcon, // Custom marker icon URL
			//   shadowUrl: markerShadow, // Custom marker shadow URL
			iconSize: [80,112], // size of the icon
			// shadowSize: [50, 64], // size of the shadow
			iconAnchor: [40, 90], // point of the icon which will correspond to marker's location
			shadowAnchor: [4, 62],  // the same for the shadow
			popupAnchor: [-3, -76]
		});

		const tourData = [
			{ date: new Date('2023-07-01'), latLng: L.latLng(51.5, -0.09), venue: 'Venue 1' },
			{ date: new Date('2023-07-15'), latLng: L.latLng(51.55, -0.1), venue: 'Venue 2' },
			// Add more tour dates...
		  ];

		const timelineControl = L.timelineControl({
			formatOutput: (date) => date.toDateString(), // Custom function to format the date output
		  });

		  // Add the timeline control to the map
		  timelineControl.addTo(map);

		  // Add the tour data to the timeline control
		  timelineControl.addTimelines({
			timeline: tourData,
			options: {
			  formatOutput: (date) => date.toDateString(), // Custom function to format the date output
			  getInterval: (entry) => entry.date,
			  pointToLayer: (entry, latLng) => L.marker(latLng).bindPopup(entry.venue),
			  onEachFeature: (entry, layer) => {
				// Customize the behavior of the markers or polygons on the timeline
			  },
			},
		  });

		markerData.forEach((marker) => {
			const { lat, lon, city } = marker;
			const markerOptions = { icon: customIcon };

			const mapMarker = new Marker([lat, lon], markerOptions)
				.bindPopup(`<b>${city}</b><br>`)
				.addTo(map);
		});

		return () => {
			map.remove();
		};
	}, []);

	return <div id="map" style={{ height: '400px' }}></div>;
};

export default Map;
